mapper.select:
SELECT COUNT(*) AS role_count
from(
        SELECT su.username,sur.role_id
        FROM sys_user su
        JOIN sys_user_role sur ON su.user_id=sur.user_id
        WHERE su.username=#{username} and role_id=7
    ) AS user_roles;

select count(*) FROM tb_user_customer where
    user_id=#{userId}
    and
    customer_id=#{customerId}
