mapper.select:
select * from sys_menu where parent_id=0 and menu_id in(
select menu_id from sys_role_menu where role_id in(
select role_id from sys_user_role where user_id in (
select user_id from sys_user where username=#{userName}
)
)
)

select * from sys_menu where parent_id=#{menuId} and menu_id in(
    select menu_id from sys_role_menu where role_id in(
        select role_id from sys_user_role where user_id in (
            select user_id from sys_user where username=#{userName}
        )
    )
)
